let zen = function () {
  const t = Snap.select(".logo-loader");
  t.attr({ fill: "#AED5FF" }), t.select(".zenman").transform("s.2,t98,105");
  const n = Snap.select(".circle"),
    o = [n],
    r = function (t, n, o) {
      const e = t.select(".dots"),
        s = e.transform().localMatrix.d;
      e.stop().animate(
        { transform: "s" + s + ", t0,0r" + 360 * o },
        (n / 100) * 1e5,
        function () {
          e.transform("s" + s + ",t0,0r0"), r(t, n);
        }
      );
    };
  for (let t = 0; t <= 5; t++) {
    o.push(n.clone());
    let e = t / 5,
      s = Math.floor(100 * Math.random()) + 25,
      a = Math.random() < 0.5 ? -1 : 1,
      l = 0.9 * e;
    o[t].attr({ fill: "#874010" }),
      o[t].select(".dots").transform("s" + l + ",t0,0r0"),
      r(o[t], s, a);
  }
};
zen();
